import React from 'react';
import {
  BrowserRouter,
  Routes,
  Route,
  Navigate,
  Outlet,
} from 'react-router-dom';
import { AuthProvider, useAuth } from './hooks/useAuth';
import routes from './routes';

function PrivateRoute() {
  const { user } = useAuth();
  return user ? <Outlet /> : <Navigate to="/login" replace />;
}

export default function App() {
  return (
    <BrowserRouter>
      <AuthProvider>
        <Routes>
          {/* Public Route */}
          <Route path="/login" element={<routes.login />} />

          {/* Private Routes Wrapper */}
          <Route element={<PrivateRoute />}>
            <Route path="/segments/builder" element={<routes.segmentBuilder />} />
            <Route path="/segments" element={<routes.segmentList />} />
            <Route path="/campaigns/new" element={<routes.campaignForm />} />
            <Route path="/campaigns/:id" element={<routes.campaignDetail />} />
            <Route path="/campaigns" element={<routes.campaignList />} />
            <Route path="/" element={<routes.dashboard />} />
          </Route>

          {/* Catch-all redirect */}
          <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>
      </AuthProvider>
    </BrowserRouter>
  );
}
