import React from 'react';
import { Switch, Route, Redirect } from 'react-router-dom';
import { AuthProvider, useAuth } from './hooks/useAuth';
import routes from './routes';

function PrivateRoute({ component: Component, ...rest }) {
  const { user } = useAuth();
  return (
    <Route
      {...rest}
      render={props =>
        user ? (
          <Component {...props} />
        ) : (
          <Redirect to="/login" />
        )
      }
    />
  );
}

export default function App() {
  return (
    <AuthProvider>
      <Switch>
        <Route path="/login" component={routes.login} />
        <PrivateRoute path="/segments/builder" component={routes.segmentBuilder} />
        <PrivateRoute path="/segments" component={routes.segmentList} exact />
        <PrivateRoute path="/campaigns/new" component={routes.campaignForm} />
        <PrivateRoute path="/campaigns/:id" component={routes.campaignDetail} />
        <PrivateRoute path="/campaigns" component={routes.campaignList} exact />
        <PrivateRoute path="/" component={routes.dashboard} exact />
      </Switch>
    </AuthProvider>
  );
}
